import styled from 'styled-components'

export const StyledTitle = styled.h1(
  ({ size, color }) => `
  height: 17px;
  margin-top: 1px;
  width: 319px;
  color: #${color};
  font-size: ${size}px;
  font-weight: 700;
  letter-spacing: NaNpx;
  line-height: 17px;

  @media only screen and (max-width: 412px) {
    max-width: 412px;
    padding-left: 0px;
    padding-right: 0px;
  }

  @media only screen and (min-width: 412px) and (max-width: 736px) {
    max-width: 736px;
    margin-left: 30px;
  }
`
)
